import React from 'react';
import {Pagination} from "react-bootstrap";
import {useSelector} from "react-redux";

const MAX_PAGES = 205;

const PageNav = () => {
    const page = useSelector(state => state.page.page);

    const getNumPagesBefore = () => {
        if (page < 6) {
            return page - 1
        }
        return 5;
    }

    const getNumPagesAfter = () => {
        if (page > 200) {
            return MAX_PAGES - page
        }
        return 5;
    }

    return (
        <div className={"justify-content-center text-center"}>
        <Pagination>
            <Pagination.Prev disabled={page === 1}/>
            {
                Array(getNumPagesBefore()).fill(0).map(
                    (e, i) => <Pagination.Item key={i}>{i + 1}</Pagination.Item>
                )
            }
            <Pagination.Item active>{page}</Pagination.Item>
            {
                Array(getNumPagesAfter()).fill(0).map(
                    (e, i) => <Pagination.Item key={i}>{page + i + 1}</Pagination.Item>
                )
            }
            <Pagination.Next disabled={page === MAX_PAGES}/>
        </Pagination>
            </div>
    )
};

export default PageNav;
